sys_script_include.script:
var fill_in_job_description = Class.create();
fill_in_job_description.prototype = Object.extendsObject(global.AbstractAjaxProcessor, {
	fill_in_job_description: function() {
		var gr = new GlideRecord('x_269830_job_appli_job_listing');
		var a = this.getParameter('sysparam_which_job');
		gr.addQuery('sys_id', a); 
		gr.query(); 
		if (gr.next()) {
			var result = this.newItem("result");
			result.setAttribute("job_description", gr.job_description);		
		}	
	}   
});
