sys_script_include.script:
var fill_in_job_description_long = Class.create();
fill_in_job_description_long.prototype =  Object.extendsObject(AbstractAjaxProcessor, {
		fill_in_job_description_long: function() {
	var gr = new GlideRecord('x_269830_job_appli_job_listing');
	var a = this.getParameter('which_job');
	gr.addQuery('job_description','=', a); 
	gr.query(); 
	if (gr.next()) {
		var result = this.newItem("result");
		result.setAttribute("job_description_long", gr.job_description_long);		
	}	
    }   
});
